void mainImage(out vec4 fragColor, in vec2 fragCoord) 
{
    vec2 uv = fragCoord / iResolution.xy;
    vec4 videoColor = texture(iChannel0, uv);
    vec4 backgroundColor = texture(iChannel1, uv);

    vec3 verde = vec3(0.0, 1.0, 0.0);
    vec3 quaseVerde = vec3(0.1, 1.0, 0.1);
    float mascara = 0.0;

    if (videoColor.r >= verde.r && videoColor.r <= quaseVerde.r)
    {
        mascara = 1.0;
    }
    if (videoColor.g >= verde.g && videoColor.g <= quaseVerde.g)
    {
        mascara = 1.0;
    }
    if (videoColor.b >= verde.b && videoColor.b <= quaseVerde.b)
    {
        mascara = 1.0;
    }

    mascara = 1.0 - mascara;
    vec3 finalColor = mix(backgroundColor.rgb, videoColor.rgb, mascara);
    fragColor = vec4(finalColor, 1.0);
}
