#define PI 3.141518
#define divs 9.

mat2 ligmify(in vec2 uv, in float angle) {

 uv.y += 0.5;
 float s = (uv.y * divs);

 mat2 scale = mat2(
     s, 0.0,
     0.0, s 
 );

 mat2 rot = mat2(
  cos(angle), -sin(angle),
  sin(angle), cos(angle)
 );

 return scale*rot;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv_raw = fragCoord / iResolution.xy;
    vec2 uv = (2.* fragCoord - iResolution.xy) / iResolution.y;

    uv *= ligmify(uv_raw, PI * iTime*0.1);

    float index = floor(uv.x) + floor(uv.y) * divs;
    float isEven = 1. - step(mod(index, 2.), .5);

    fragColor = vec4(vec3(isEven), 1.0);
}
