void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.y * 10.0 - vec2(iResolution.x / iResolution.y, 5.0);

    // Time varying pixel color
    vec3 col = vec3(
        0.5 / abs(sin(uv.x - (iTime + 1.0) * 0.6931471805) * sin(iTime + 1.0 * 0.6931471805) - uv.y),
        0.5 / abs(sin(uv.x - (iTime + 1.0) * 2.7182818284) * sin(iTime + 1.0 * 2.7182818284) - uv.y),
        0.5 / abs(sin(uv.x - (iTime + 1.0) * 3.1415926535) * sin(iTime + 1.0 * 3.1415926535) - uv.y)
    );

    // Output to screen
    fragColor = vec4(col,1.0);
}
