void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    vec2 resolution = iResolution.xy;
    float time = iTime * 0.7; 
    float squareSize = 0.1 * resolution.x;
    float speed = 0.9;
    vec2 square1 = vec2(0.5 + 0.3 * cos(speed * time), 0.5 + 0.3 * sin(speed * time));
    vec2 square2 = vec2(0.5 + 0.4 * cos(speed * time), 0.5 + 0.4 * sin(speed * time));
    vec2 square3 = vec2(0.5 + 0.1 , 0.5 + 0.1);
    vec3 color1 = vec3(1.0, 0.5, 0.2); 
    vec3 color2 = vec3(0.2, 0.5, 1.0); 
    vec3 color3 = vec3(0.7, 0.2, 0.5); 
    vec3 backgroundColor = 0.9 +0.1 + 0.1 * sin(50.*time + fragCoord.xyx * 0.1 + length(fragCoord.yx) * 0.1);
    vec3 finalColor = backgroundColor;
    if (abs(fragCoord.x - resolution.x * square1.x) < squareSize && abs(fragCoord.y - resolution.y * square1.y) < squareSize) {
        finalColor = color1;
    }
    if (abs(fragCoord.x - resolution.x * square2.x) < squareSize && abs(fragCoord.y - resolution.y * square2.y) < squareSize) {
        finalColor = color2;
    }
    if (abs(fragCoord.x - resolution.x * square3.x) < squareSize && abs(fragCoord.y - resolution.y * square3.y) < squareSize) {
        finalColor = color3;
    }
    fragColor = vec4(finalColor, 6.0);
}
