float DITHER_THRESHOLDS[16] = float[]
(
    0.05882, 0.52941, 0.17647, 0.64706,
    0.76471, 0.29412, 0.88235, 0.41176,
    0.23529, 0.70588, 0.11765, 0.58824,
    0.94118, 0.47059, 0.82353, 0.35294
);

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

    vec2 positionSS = mod(fragCoord.xy , 4.0);
    int index = int(positionSS.x) * 4 + int(positionSS.y);
    float alpha = DITHER_THRESHOLDS[index];
    float clip = step(alpha, 0.4);
    vec3 col = vec3(clip, clip, clip);
    fragColor = vec4(col,1.0);
}
