vec3 palette[6] = vec3[6](
    vec3(0.2, 0.3, 0.5),    // Dark Slate Blue
    vec3(0.9, 0.1, 0.1),    // Bright Red
    vec3(0.1, 0.8, 0.1),    // Bright Green
    vec3(0.1, 0.5, 0.8),    // Bright Blue
    vec3(0.9, 0.8, 0.0),    // Bright Yellow
    vec3(0.8, 0.2, 0.8));

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord / iResolution.y;

    vec2 a = uv*9.0;

    int A = int(1.5*sin(fract(a.x*0.25+sin(uv.y*(1.3+sin(iTime))*1.0+iTime*3.0)/4.0)*3.14));

    vec3 d[2];

    d[0] = palette[int(fract((uv.x+uv.y)*20.0) + fract((uv.x-uv.y)*20.0))];
    d[1] = palette[2+int(iTime*2.0+round(sin(uv.x*200.0)*0.5+sin(uv.y*200.0)*0.5))%4];


    fragColor = vec4(d[A], 1.0);
}
