void mainImage( out vec4 o, in vec2 FC )
{
float i,j,d=.1,e,t=iTime; vec3 p,q=vec3(1,0,cos(t)),r=iResolution;
o=vec4(0.);

for(i=0.;i++<64.&&d>1e-4;){
    p=vec3((FC.xy-.5*r.xy)/r.y,cos(t/e))/1.5;
    p.z-=1.1;
    p.xy-=.3;

    for(j=0.;j++<16.;)
        p=abs(p*e-.03)-.02,
        d=max(min(p.x,p.z),p.y/d),
        d-=dot(p,q)/1e4;

    e-=d;

}o+=3./i;
}
