void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    // Time varying pixel color
    vec3 col = texture(iChannel0,uv).rgb;

    int Min;
    if(min(min(col.r,col.g),col.b)==col.r){
        Min=0;
    }else if(min(min(col.r,col.g),col.b)==col.g){
        Min=1;
    }else{
        Min=2;
    }

    int Max;
    if(max(max(col.r,col.g),col.b)==col.r){
        Max=0;
    }else if(max(max(col.r,col.g),col.b)==col.g){
        Max=1;
    }else{
        Max=2;
    }

    int Mid;
    if((Max+Min)==1){
        Mid=2;
    }else if((Max+Min)==2){
        Mid=1;
    }else{
        Mid=0;
    }

    float Cols[3]=float[3](
        col.r,
        col.g,
        col.b
    );

    float a = Cols[Max]-Cols[Min];
    float b = Cols[Mid]-Cols[Min];
    float x = Cols[Max];


    Cols[Max]=x;
    Cols[Min]=0.;
    Cols[Mid]=x*b/a;

    col.r=Cols[0];
    col.g=Cols[1];
    col.b=Cols[2];

    // Output to screen
    fragColor = vec4(col,1.0);
}
