#define rot(a) mat2(cos(a),sin(a),-sin(a),cos(a))
float orbit;
#define n1Q(p) p *= 2.*ri(round(atan(p.x, p.y) * 4.) / 4.)
#define ri(a) mat2(cos(a + asin(vec4(0,1,-1,0))))
float map(vec3 p){
	p.yz*=rot(iTime*0.1);
  	p.xz*=rot(iTime*0.2);
	p=abs(p)-3.5;

	if(p.x<p.z)p.xz=p.zx;
	if(p.y<p.z)p.yz=p.zy;
 	if(p.x<p.y)p.xy=p.yx;
 	float scale=-5.5;
	float mr2=.57;
	float s=2.5;
	vec3  p0=p*1.5;
     n1Q(p0.xy);

	for (float i=0.;i<3.+fract(iTime*.2)*5.;i++){
		p=1.-abs(p-1.);

  		float k=scale*clamp(mr2*max(1.2/dot(p,p),.8),0.,1.);
    	s*=abs(k);
   		p*=k;
        p+=p0;

    	p.yz*=rot(.05);
    }
	orbit = log2(s);
     return length(cross(p,normalize(vec3(1))))/s-.003;

}

float map2(vec3 p){
	p.yz*=-rot(iTime*0.1);
  	p.xz*=rot(iTime*0.2);
	p=abs(p)-3.5;

	if(p.x<p.z)p.xz=p.zx;
	if(p.y<p.z)p.yz=p.zy;
 	if(p.x<p.y)p.xy=p.yx;
 	float scale=-5.5;
	float mr2=.57;
	float s=2.5;
	vec3  p0=p*1.5;
     n1Q(p0.xy);
	for (float i=0.;i<3.+fract(iTime*.2)*5.;i++){
		p=1.-abs(p-1.);

  		float k=scale*clamp(mr2*max(1.2/dot(p,p),.8),0.,1.);
    	s*=abs(k);
   		p*=k;
        p+=p0;

    	p.yz*=rot(-.05);
    }
	orbit = log2(s);
     return length(cross(p,normalize(vec3(1))))/s-.003;

}
float map3(vec3 p){

  	 p.yx*=rot(iTime);
        p.yz*=rot(iTime);
	p=abs(p)-3.5;

	if(p.x<p.z)p.xz=p.zx;
	if(p.y<p.z)p.yz=p.zy;
 	if(p.x<p.y)p.xy=p.yx;
 	float scale=-5.5;
	float mr2=.57;
	float s=2.5;
	vec3  p0=p*1.5;
     n1Q(p0.xy);
	for (float i=0.;i<3.+fract(iTime*.2)*5.;i++){
		p=1.-abs(p-1.);

  		float k=scale*clamp(mr2*max(1.2/dot(p,p),.8),0.,1.);
    	s*=abs(k);
   		p*=k;
        p+=p0;
         p.yx*=rot(0.05);

    }
	orbit = log2(s);
     return length(cross(p,normalize(vec3(1))))/s-.003;

}
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv=(2.*fragCoord-iResolution.xy)/iResolution.y;
      vec2 uv2=(2.*fragCoord-iResolution.xy)/iResolution.y;
  vec2 uv3=(2.*fragCoord-iResolution.xy)/iResolution.y;
  	vec3 ro=vec3(0,0,10.+5.*cos(iTime));
  	vec3 rd=normalize(vec3(uv,-3));
    vec3 ro2=vec3(0,0,20.+5.*sin(iTime));
  	vec3 rd2=normalize(vec3(uv2,-3));
    vec3 col;

       vec3 ro3=vec3(0,0,30.+5.*sin(iTime));
  	vec3 rd3=normalize(vec3(uv3,-3));
    vec3 col3;
     vec3 col2;
	float h=0.,d,i;
	for(i=1.;i<100.;i++)
    {
    	vec3 p=ro+rd*h;
		d=map(p);
        if(d<.0001){
    		col=80.*(vec3(1,0,2)*cos(orbit*15.)+cos(abs(p)*.7))/i;
            break;
        }
    	h+=d;
	}

    float h2=0.,d2,i2;
    for(i2=1.;i2<100.;i2++)
    {
    	vec3 p2=ro2+rd2*h2;
		d2=map2(p2);
        if(d2<.0001){
    		col2=80.*(vec3(1,0,0)*cos(orbit*15.)+cos(abs(p2)*.7))/i;
            break;
        }
    	h2+=d2;
	}


     float h3=0.,d3,i3;
    for(i3=1.;i3<100.;i3++)
    {
    	vec3 p3=ro3+rd3*h3;
		d3=map3(p3);
        if(d3<.0001){
    		col3=80.*(vec3(1,5,2)*cos(orbit*15.)+cos(abs(p3)*.7))/i3;
            break;
        }
    	h3+=d3;
	}
    fragColor.xyz=clamp(col3+col+col2,0.0,1.0);
}
