float f(float x, float t, float c, float b){
    float a = -abs(mod((2.*x)/c-t, 2.)-1.)+1.;
    float d = (1.-b)/2.;
    return (abs(a-0.5+d)-abs(a-0.5-d))/2.+0.5;
}

float Wave(float x, float t, float c, float b){
    return (f(x, t, c, b)-(b/2.))/(1.-b);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy-0.5;
    uv.x *= iResolution.x/iResolution.y;

    float d = length(uv-Pos);

    vec3 col = vec3(Wave(d, time_multiplier*iTime, wave_length, 1.-wave_sharpness));

    fragColor = vec4(col,1.0);
}
