// Rendering flag of Poland to celebrate Polish independence

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    // Colors specified in Polish law converted from hex values
    const vec3 white = vec3(0.914,0.91,0.906);
    const vec3 red = vec3(0.831,0.129,0.239);

    // Two equal horizontal stripes
    vec3 col = mix(red, white, step(0.5, uv.y));

    // Output to screen
    fragColor = vec4(col,1.0);
}
