void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;
    float n = 15.0;

    float colorSpace = uv.x;
    if (uv.y > 0.5)
    {
        // Convert from Linear to sRGB color
        //colorSpace = pow(colorSpace, 1.0 / 2.2);
        colorSpace = mod(fragCoord.x,2.0);
    }
    else
    {
        colorSpace = floor(colorSpace * n) / n;
    }

    // Output to screen
    fragColor = vec4(vec3(colorSpace),1.0);
}
