// 3D version of the classic Microsoft "Pipe Dream" screensaver, infinite version
// Big thanks to @morimea for many tips on improving the first 
// version of this shader, specifically using voxels for each 
// pipe point instead of a very slow for loop when drawing the pipes,
// and for the camera rotation script.
// Also thanks @iq for help cleaning up the big code unroll in buffer A
// and reducing compile time tremendously.

void getFade(out float fade, out int iter) {

    iter = iFrame / (duration + fadeDuration);

    fade = 1.0 - (float(max((iFrame - (duration+fadeDuration)*iter) - duration, 0)) / float(fadeDuration));

}

Surface getDist(vec3 p, vec3 rd) {

    int iter = iFrame / (duration + fadeDuration);

    // 3D repetition from iquilezles.org/articles/distfunctions/
    p += vec3(vec2(float(gridSize)/2.0), float(gridSize) * -1.0);
    vec3 q = fract(p)-0.5;

    // Overstep correction
    Surface dCS = Surface(0.25, vec3(0.0));

    // Get point state from buffer
    int x = int(mod(p.x, float(gridSize))) + int(mod(p.z, float(gridSize)))*gridSize;
    int y = int(mod(p.y, float(gridSize * 5)));
    y = int(mod(float(y) + float(gridSize) * (floor(p.x / float(gridSize))), float(gridSize * 5)));
    y = int(mod(float(y) + float(gridSize) * (floor(p.z / float(gridSize))), float(gridSize * 5)));
    vec3 state = texelFetch(iChannel0, ivec2(x,y), 0).rgb;

    int id = int(state.r * float(numPipes));

    if (id != 0) {

        // Get point attributes
        bool pipeJoint = fract(state.g) == 0.5;

        int reachDir = int(state.b * 0.1);
        vec3 rd = reachDir == 1 ? vec3(0.0, 1.0, 0.0) :
        reachDir == 2 ? vec3(0.0, -1.0, 0.0) : 
        reachDir == 3 ? vec3 (-1.0, 0.0, 0.0) :
        reachDir == 4 ? vec3(1.0, 0.0, 0.0) :
        reachDir == 5 ? vec3(0.0, 0.0, -1.0) :
        reachDir == 6 ? vec3(0.0, 0.0, 1.0) : vec3(0.0);

        int joinDir = int(mod(state.b, 10.0));
        vec3 jd = joinDir == 1 ? vec3(0.0, -1.0, 0.0) :
        joinDir == 2 ? vec3(0.0, 1.0, 0.0) : 
        joinDir == 3 ? vec3 (1.0, 0.0, 0.0) :
        joinDir == 4 ? vec3(-1.0, 0.0, 0.0) :
        joinDir == 5 ? vec3(0.0, 0.0, 1.0) :
        joinDir == 6 ? vec3(0.0, 0.0, -1.0) : vec3(0.0);

        vec3 col = color(state.r, iter + int(p.x / float(gridSize)) + int(p.y / float(gridSize)) + int(p.z / float(gridSize)));

        Surface rc = sdCapsule(q, vec3(0.0), rd, pipeRadius, col);
        Surface jc = sdCapsule(q, vec3(0.0), jd, pipeRadius, col);

        if (pipeJoint) {

            Surface sphere = sdSphere(q, capRadius, col);

            return surfaceMin(surfaceMin(sphere, surfaceMin(rc, jc)), dCS);

        }

        return surfaceMin(surfaceMin(rc, jc), dCS);

    }

    return surfaceMin(Surface(float(gridSize) * 0.05, vec3(0.0)), dCS);

    // Debug grid
    //return surfaceMin(sdSphere(q, pipeRadius, vec3(0.0)), dCS);

}

// Raymarch
Surface rayMarch(vec3 ro, vec3 rd){

    float dO = 0.0;

    vec3 col = vec3(0.0);
    vec3 p;
    Surface distColor;

    for (int i=0; i<MAX_STEPS; i++){

        p = ro + rd*dO;

        distColor = getDist(p, rd);
        float dS = distColor.dist;

        dO += dS;
        if (dO>MAX_DIST || dS<SURFACE_DIST) break;
    }

    col = distColor.col;

    return Surface(dO, col);

}

// Get normal
vec3 getNormal(vec3 p, vec3 rd) {

    float d = getDist(p, rd).dist;
    vec2 e = vec2(.01, 0.0);

    vec3 n = d - vec3(
        getDist(p-e.xyy, rd).dist,
        getDist(p-e.yxy, rd).dist,
        getDist(p-e.yyx, rd).dist
    );

    return normalize(n);

}

