#ifdef AA
void mainImageRaw(out vec4 fragColor, in vec2 fragCoord)
#else
void mainImage(out vec4 fragColor, in vec2 fragCoord)
#endif
{

    vec2 uv = (fragCoord-.5*iResolution.xy)/iResolution.y;

    // Fade amount and iteration
    float fade;
    int iter;
    getFade(fade, iter);

    vec3 col = vec3(0.0);

    // Ray origin
    vec3 ro = vec3(0.0);
    // Ray direction
    vec3 rd = normalize(vec3(uv.x, uv.y, 1.0));

    ro = vec3(0.0, 0.0, float(gridSize) * 1.5); 
    vec2 m = (iMouse.xy/iResolution.xy-0.5)*3.14159265*vec2(2.0,1.0); // mouse

    float timer = fract(iTime / 30.0);
    //moving cam
    ro += 0.75*float(gridSize) * vec3(sin(timer * (3.1415926 * 0.5)), 0.0, cos(timer  *(3.1415926 * 0.5)));
    //static cam
    //ro += float(gridSize) * vec3(0.75, 0.0, 0.0);

    if(iMouse.z < 1.0)
    {
        // anim
        m = vec2(3.1415926, 0.0);
        m.x *= -cos(0.15 * timer * (3.1415926 * 2.0));
    }

    m.y = -m.y;
    mat3 rotX = mat3(1.0, 0.0, 0.0, 0.0, cos(m.y), sin(m.y), 0.0, -sin(m.y), cos(m.y));
    mat3 rotY = mat3(cos(m.x), 0.0, -sin(m.x), 0.0, 1.0, 0.0, sin(m.x), 0.0, cos(m.x));
    rd = (rotY * rotX) * rd;

    // Raymarch and get diffuse color
    Surface rs = rayMarch(ro, rd);
    float d = rs.dist;
    col = rs.col;

    vec3 p = (ro + rd * d);

    // Diffuse lighting
    /*vec3 diffuse = getLight(p, vec3(1.0), vec3(2.0), rd);
    diffuse = clamp(diffuse, 0.15, 1.0);
    diffuse = d == 0.0 ? vec3(0.0) : diffuse;

    vec3 color = vec3(clamp(diffuse * col, 0.0, 1.0));*/

    // Normal lighting
    vec3 normal = getNormal(p, rd);
    float normalLight = clamp((normal.r)*0.33 + (1.0-normal.b)*0.33 + normal.g*0.33, 0.25, 1.0);
    if (d > float(gridSize)*10.0) normalLight = 0.0;   

    vec3 color = vec3(clamp(normalLight * col + normalLight * 0.25, 0.0, 1.0));

    //fragColor = vec4(d*0.03);
    //fragColor = vec4(vec3(diffuse), 1.0);
    //fragColor = vec4(vec3(normalLight), 1.0);
    //fragColor = vec4(col, 1.0);
    fragColor = vec4(color * fade, 1.0);

    // Buffer debug
    //fragColor = texture(iChannel0, fragCoord/iResolution.xy * 0.1);

}

#ifdef AA
// Antialiasing "module" by FabriceNeyret2 https://www.shadertoy.com/view/WlfyW8
void mainImage(out vec4 O, vec2 U) {
    vec4 T;  
    O = vec4(0);                                          
    for (int k=0; k<AA*AA; k++, O+=T) {              
        mainImageRaw(T, U + 0.33 * vec2(k%AA - AA/2, k/AA - AA/2));
    }
    O /= float(AA*AA);
}
#endif

// Initialize grid and start points
void initGrid(inout vec4 fragColor, in ivec2 ipx, in int iter) {

    vec3 point;

    // Unique start points, one per pipe
    int startPoints[numPipes];
    const int maxPoints = int(gridSize*gridSize*gridSize);
    int allPoints[maxPoints];

    for (int i=0; i < maxPoints; i++) {
        allPoints[i] = i;
    }

    for (int i=0; i < numPipes; i++) {
        int randomStart = int(floor(random(vec2(1.0 + float(i) + float(iter) * randomSeed)) * float(maxPoints - i)));
        startPoints[i] = allPoints[randomStart];
        allPoints[randomStart] = allPoints[maxPoints - (1+i)];
    }

    int pointIncrement = 0;

    // Populate grid with available points and start points
    for (int i=0; i < gridSize*gridSize*gridSize; i++) {

        int x = int(mod(float(i), float(gridSize)));
        int y = int(floor(float(i) / float(gridSize * gridSize)));
        int z = int(floor((float(i) - float(y * gridSize * gridSize)) / float(gridSize)));

        int startPoint = 0;

        for (int i=0; i < numPipes; i++) {
            if (pointIncrement == startPoints[i]) {
                startPoint = i + 1;
            }
        }

        if (
            ipx.x % gridSize == x
            && ipx.y == y
            && ipx.x / gridSize == z
            && startPoint != 0
        )
        {
            point = vec3(float(startPoint) / float(numPipes), 1.5, 0.0);
        }

        pointIncrement++;

    }

    if (point.r != 0.0) fragColor = vec4(point, 1.0);

}

// Possible directions for next point
const ivec3 up = ivec3(0, 1, 0);
const ivec3 down = ivec3(0, -1, 0);
const ivec3 left = ivec3(-1, 0, 0);
const ivec3 right = ivec3(1, 0, 0);
const ivec3 back = ivec3(0, 0, -1);
const ivec3 front = ivec3(0, 0, 1);

const int directionCount = 6;

const ivec3 directions[directionCount] = ivec3[](up, down, left, right, back, front);

// Update grid points
void updateGrid(inout vec4 fragColor, in ivec2 ipx, in int vn) {

    // Get point states
    bool pointStates[int(gridSize*gridSize*gridSize)];

    for (int i=0; i < int(gridSize*gridSize*gridSize); i++) {
        pointStates[i] = true;
    }

    ivec3 nextPoint;

    // Find active points
    for (int i=0; i < gridSize*gridSize*gridSize; i++) {

        int x = int(mod(float(i), float(gridSize)));
        int y = int(floor(float(i) / float(gridSize * gridSize)));
        int z = int(floor((float(i) - float(y * gridSize * gridSize)) / float(gridSize)));     

        vec3 state = texelFetch(iChannel0, ivec2(float(x+z*gridSize)+0.5, float(y+vn*gridSize)+0.5), 0).rgb;

        if (state.g > 0.5) {

            int randomDirIter = 0;

            int directionKey = 0;

            // Generate next point
            ivec3 dirDynamic[directionCount] = directions;
            if (y == gridSize - 1) dirDynamic[0] = down;
            if (y == 0) dirDynamic[1] = up;
            if (x == 0) dirDynamic[2] = right;
            if (x == gridSize - 1) dirDynamic[3] = left; 
            if (z == 0) dirDynamic[4] = front;
            if (z == gridSize - 1) dirDynamic[5] = back;

            bool validNextPoint = false;

            for (int i=0; i < directionCount; i++) {
                float newRandom = random(vec2(x + y + randomDirIter + iFrame)) * 0.999;
                int randomDir = int(floor(newRandom*float(directionCount-i-1)));
                nextPoint = ivec3(x,y,z) + dirDynamic[randomDir];
                vec3 nextPointState = texelFetch(iChannel0, ivec2(float(nextPoint.x + nextPoint.z*gridSize)+0.5, float(nextPoint.y+vn*gridSize)+0.5), 0).rgb;
                // Check point availability
                if (nextPointState.r == 0.0 && pointStates[int(nextPoint.x + nextPoint.y*gridSize + nextPoint.z*gridSize*gridSize)]) {
                    validNextPoint = true;
                    // Mark point as taken
                    pointStates[int(nextPoint.x + nextPoint.y*gridSize + nextPoint.z*gridSize*gridSize)] = false;
                    directionKey = dirDynamic[randomDir] == up ? 1 :
                       dirDynamic[randomDir] == down ? 2 :
                       dirDynamic[randomDir] == left ? 3 :
                       dirDynamic[randomDir] == right ? 4 : 
                       dirDynamic[randomDir] == back ? 5 : 
                       dirDynamic[randomDir] == front ? 6 : 0;
                    break;
                }

                dirDynamic[randomDir] = dirDynamic[directionCount-i-1];
                randomDirIter++;

            }

            // Update next point
            if (
                validNextPoint
                && ipx.xy == ivec2(nextPoint.x + nextPoint.z*gridSize, nextPoint.y)
            )
            {

                fragColor = vec4(state.r, 1.0, directionKey, 1.0);
            }

            // Update this point
            if (
                ipx.xy == ivec2(x+z*gridSize, y)
            )
            {
                fragColor = vec4(state.r, 0.5, state.b, 1.0);
                if (validNextPoint) {
                    float thisPointDir = mod(state.b, 10.0) + float(directionKey)*10.0;
                    fragColor = vec4(state.r, fract(state.g), thisPointDir, 1.0);
                    if (directionKey != int(state.b) && random(vec2(nextPoint.xy + nextPoint.xz + nextPoint.yz)) > 0.5) {
                        fragColor = vec4(state.r, 0.5, thisPointDir, 1.0);
                    }
                } 
            }

        }

    }

}

// Stop all pipes
void stopPipes(inout vec4 fragColor, in ivec2 ipx, in int vn) {

    // Find active points
    for (int i=0; i < gridSize*gridSize*gridSize; i++) {

        int x = int(mod(float(i), float(gridSize)));
        int y = int(floor(float(i) / float(gridSize * gridSize)));
        int z = int(floor((float(i) - float(y * gridSize * gridSize)) / float(gridSize)));

        vec3 state = texelFetch(iChannel0, ivec2(float(x+z*gridSize)+0.5, float(y+vn*gridSize)+0.5), 0).rgb;

        if (
            int(mod(float(ipx.x), float(gridSize))) == x
            && ipx.y == y
            && ipx.x / gridSize == z
            && state.g > 0.5
        )
        {
            fragColor = vec4(state.r, 0.5, state.b, 1.0);
        }

    }

}

void mainImage(out vec4 fragColor, in vec2 fragCoord)
{

    if (fragCoord.x > float(gridSize*gridSize) || fragCoord.y > float(gridSize*5)) discard;

    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    // Current iChannel0
    vec4 previousTex = texture(iChannel0, uv);

    // Iteration
    int iter = iFrame / (duration + fadeDuration);

    // Draw buffer texture
    fragColor = previousTex;
    bool stopCondition = false;

    // Int fragCoord
    ivec2 ipx = ivec2(fragCoord);

    // Initialize
    if (iFrame == 0 || iFrame % (duration + fadeDuration) == 0)
    {

        fragColor = vec4(0.0);
        for(int i = min(iFrame, 0); i < 5; i++)
            initGrid(fragColor, ivec2(ipx.x, ipx.y - gridSize*i), iter + i);
        stopCondition = true;

    }

    // Stop pipes at duration
    if ((iFrame - iter*fadeDuration) % duration == 0 && !stopCondition)
    {
        for(int i = min(iFrame, 0); i < 5; i++)
        stopPipes(fragColor, ivec2(ipx.x, ipx.y - gridSize*i), i);
        stopCondition = true;
    }

    // Update every s frames
    if (iFrame % speed == 0 && !stopCondition)
    {
        for(int i = min(iFrame, 0); i < 5; i++)
        {
            updateGrid(fragColor, ivec2(ipx.x, ipx.y - gridSize*i), i);
        }
    }

}

// Global Variables //

// Max FPS
#define fps 45
// Cube grid size
#define gridSize 6
// Random seed (for start positions, directions, and colors)
#define randomSeed 44.24
// Number of pipes
#define numPipes 3
// Speed (frames between updates)
#define speed int(6.0 * max((float(iFrame) / max(iTime, 0.01)) / float(fps), 1.0))
// Duration (length of one arrangement in frames)
#define duration int(360.0 * max((float(iFrame) / max(iTime, 0.01)) / float(fps), 1.0))
// Fade (length of fade out)
#define fadeDuration int(30.0 * max((float(iFrame) / max(iTime, 0.01)) / float(fps), 1.0))
// Pipe radius
#define pipeRadius 0.1
// Cap radius
#define capRadius 0.15

// Raymarching Globals //

#define MAX_STEPS 400
#define  MAX_DIST 300.0
#define SURFACE_DIST 0.005

// Antialiasing level (uncomment to enable) //
//#define AA 4

// Global Functions //

// Random function from the Book of Shaders - returns random value between 0 and 1
float random (in vec2 uv) {

    return fract(sin(dot(uv.xy, vec2(12.98,78.23))) * randomSeed);

}

// Randomly generate colors
vec3 color(in float p, in int iter) {

    float ti = float(iter) * random(vec2(p+1.));

    float r = random(vec2(p+ti,p+ti+1.));
    float g = random(vec2(p+ti+2.,p+ti+3.));
    float b = random(vec2(p+ti+4.,p+ti+5.));

    vec3 col = vec3(r,g,b);
    return col;

}

// Raymarched surface constructor
struct Surface {

    float dist;
    vec3 col;

};

// Surface min
Surface surfaceMin(Surface a, Surface b) {

    vec3 c = a.dist > b.dist ? b.col : a.col;

    return Surface(min(a.dist, b.dist), c);

}

// Raymarched sphere (iquilezles.org/articles/distfunctions)
Surface sdSphere(vec3 p, float r, vec3 col) {

    return Surface(length(p)-r, col);

}

// Raymarched capsule (iquilezles.org/articles/distfunctions)
Surface sdCapsule(vec3 p, vec3 a, vec3 b, float r, vec3 col)
{

  vec3 pa = p - a, ba = b - a;
  float h = clamp(dot(pa,ba)/dot(ba,ba), 0.0, 1.0);
  return Surface(length(pa - ba*h) - r, col);

}

// Rotate
vec3 rotateY (vec3 point, vec3 pivot, float angle) {

  vec3 direction = point - pivot;
  float cosTheta = cos(angle);
  float sinTheta = sin(angle);

  mat3 rotationMatrix = mat3(
    cosTheta, 0, sinTheta,
    0, 1, 0,
    -sinTheta, 0, cosTheta
  );

  return pivot + rotationMatrix * direction;

}
