void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 EarthPos;
    vec2 MoonPos;

    float EarthDist;
    float MoonDist;

    float EarthGA;
    float MoonGA;

    float NormGA;

    vec3 colour;

    float bands = 2.0;

    float EMD = 0.9;

    EarthPos.x = iResolution.x * 0.05;
    MoonPos.x = iResolution.x * 0.95;
    EarthPos.y = iResolution.y * 0.5;
    MoonPos.y = iResolution.y * 0.5;


    EarthDist = distance(fragCoord,EarthPos)/(EMD*iResolution.x);
    MoonDist = distance(fragCoord,MoonPos)/(EMD*iResolution.x);

    EarthGA = 0.00269639/(EarthDist*EarthDist);
    MoonGA = 0.000033197/(MoonDist*MoonDist);

    colour.xyz = vec3(1.0);
    NormGA = 1.0-(EarthGA - MoonGA + 1.622)/11.41;
    colour.x = NormGA;
    if (EarthGA - MoonGA <= 0.0002 && EarthGA - MoonGA >= -0.0002)
    {
        colour.xyz = vec3(0.0);
    }
    colour.z = colour.z-50.0*((0.01/bands)*float(int((50000.0/bands)*colour.x)%int(bands)));

    vec4 K = vec4(1.0, 2.0 / 3.0, 1.0 / 3.0, 3.0);
    vec3 p = abs(fract(vec3(colour.x) + K.xyz) * 6.0 - K.www);
    vec3 rgb = colour.z * mix(K.xxx, clamp(p - K.xxx, 0.0, 1.0), colour.y);
    fragColor = vec4(rgb, 1.0);


    if(distance(fragCoord,EarthPos) <= 0.016592 * EMD * iResolution.x ||
    distance(fragCoord, MoonPos) <= 0.004518 * EMD * iResolution.x)
    {
        fragColor = vec4(1.0);
    }
}
