void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = vec2(fragCoord.x/iResolution.x*2.0*1.77 - 1.77, fragCoord.y/iResolution.y*2.0 - 1.0);
    vec2 uv_moved = uv + vec2(0.5*cos(0.5*iTime), 0.5*sin(0.5*iTime));

    float PI = 3.1415926535;

    float r = sin(2.0*length(uv_moved) - 3.0*iTime)+1.0;
    float g = sin(5.0*length(uv_moved) + (2.0*PI)/3.0 - 1.5*iTime)+1.0;
    float b = sin(10.0*length(uv_moved) + (4.0*PI)/3.0 - 2.0*iTime)+1.0;

    // Time varying pixel color
    vec3 col = vec3(r, g, b);

    // Output to screen
    fragColor = vec4(col,1.0);
}
