const float ANGLE = 1.2;
const float SCALE = 10.0;

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    mat2 m = mat2(
        cos(ANGLE) / SCALE, -sin(ANGLE) / SCALE,
        sin(ANGLE) / SCALE, cos(ANGLE) / SCALE
    );
    vec2 uv = m * fragCoord;

    uv -= floor(uv);
    uv = abs(uv - 0.5) * 2.0;
    float bd = min(distance(uv, vec2(0.0, 0.0)), distance(uv, vec2(1.0, 1.0)));
    float wd = min(distance(uv, vec2(1.0, 0.0)), distance(uv, vec2(0.0, 1.0)));

    float t = sin(fragCoord.x * 8.0 / iResolution.x + iTime) * 0.5 + 0.5;
    float d = 2.0 / SCALE;
    float g = smoothstep(t + d, t - d, wd / (wd + bd));

    g = pow(g, 1.0/2.2);
    fragColor = vec4(g, g, g, 1.0);
}
