void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float zoom = exp2(iTime * 0.5);
    vec2 divMouse = iMouse.xy / iResolution.xy * 5.0;
    float x0 = ((fragCoord.x / iResolution.y) * 2.47 - 2.0);
    float y0 = (fragCoord.y * 2.0 / iResolution.y - 1.0) * 1.12;
    vec2 scale = vec2(x0, y0) / zoom - divMouse + vec2(2.5, 2.5);
    uint d = uint(0);
    vec2 xy = vec2(0.0, 0.0);
    while(xy.x * xy.x + xy.y * xy.y <= 4.0 && d < uint(1000)) {
        vec2 mul = xy * xy;
        xy = vec2(mul.x - mul.y, 2.0 * xy.x * xy.y) + scale;
        ++d;
    }
    float d2 = fract(float(d) * 0.05 - iTime);
    fragColor = vec4(d2, scale * xy * d2, 1);
}
