float col(vec2 a, vec2 b, vec2 uv)
{
    float cos1 = dot(a, uv);
    float cos2 = dot(b, uv);
    float cos3 = dot(a, b);

    return float ((cos1) >= (cos3)) * float ((cos2) >= (cos3));
}


void mainImage(out vec4 fragColor, in vec2 fragCoord )
{

    //colors not used yet
    vec3 blue = vec3(0.216, 0.471, 0.698); // blue
	vec3 yellow = vec3(1.00, 0.329, 0.298); // yellow
	vec3 red = vec3(0.867, 0.910, 0.247); // red


    //float greenValue = abs(1. - 1./sin(iTime));

    float greenValue = abs(sin(iTime)); //max(abs(sin(iTime)),0.75);

    //x×(1−a)+y×a


    vec3 svtplayGreen = vec3(0.474, greenValue,0.250) ;


    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    vec2 top = vec2(0.4, 0.75);
    vec2 bottom = vec2(0.4, 0.25);
    vec2 right = vec2(0.575, 0.5);

    vec2 tb = top - bottom;

    vec2 tr = top - right;

    vec2 br = bottom - right;

    float q1 = col(normalize(tb), normalize(tr), -normalize(uv - top));

    float q2 = col(normalize(tb), normalize(br), -normalize(uv - bottom));

    vec3 col = (q1 - q2) * (svtplayGreen);// * (0.7 + 0.5*cos(iTime+uv.xyx));


    fragColor = vec4(col, 1.0);

}
