float drawCircle(in vec2 uv,vec2 center,float r){
    float dist=distance(uv,center);
    return smoothstep(dist,dist+0.005,r);
}

//uv扰动。对x,y进行扰动，产生圆的变形效果
vec2 uvDisturb(vec2 uv,float disfactor){
    float xDisturbFactor = (sin(iTime*3.+disfactor))*0.05+1.;
    float yDisturbFactor = (cos(iTime*3.+disfactor))*0.05+1.;
    return vec2(xDisturbFactor,yDisturbFactor);                      
}

float M_PI = 3.1415926535897932384626433832795;
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;
    uv-=0.5;
    //修正拉伸
    float aspect = iResolution.x/iResolution.y;
    uv = uv * vec2(aspect,1.); //把坐标原点移动到画布中心,并根据画布宽高比修正为正圆

    vec3 color = vec3(0.);
    vec2 center = vec2(0.5);


    vec3 colorDot=vec3(0.);
    const float dotnum=7.;
    for(float i=1.;i<dotnum;i++){
        float theta = (2.0*M_PI/dotnum)*i;
        //vec2 pos = vec2(cos(theta),sin(theta))*(1.15-fract((u_time+i*0.45)*0.45));
        vec2 pos = vec2(cos(theta),sin(theta))*(1.12-sin(fract((iTime*0.5+i*0.45))*1.57));
        float d1 = drawCircle(uv,pos,clamp(i*0.01,0.02,0.03));
        colorDot += vec3(d1);
    }
    uv+=0.5;

    //绘制外圆
    vec2 disturb = uvDisturb(uv,3.);
    // uv*=disturb;
    // center*=disturb;
    float c1 = drawCircle(uv,center,0.15);

    //绘制内圆
    disturb = uvDisturb(uv,4.);
    // uv*=disturb;
    // center*=disturb;
    float c2 = drawCircle(uv,center,0.08);

    //合成圆环
    color = vec3(c1-c2);

    // Output to screen
    fragColor = vec4(color+colorDot,1.0);
}
