void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	// t=0 - no effect, t=1 - full effect
	float t = sin(iTime) * 0.5 + 0.5;
	float dt = cos(iTime) * 0.5; // derivative of t, to control the corrosion edge.
	float u = 1.0 - t;
	// uv coordinates of the screen.
	vec2 uv = fragCoord.xy / iResolution.xy;

	// the corrosion fragment color.	
	vec4 rgba = texture(iChannel1, uv);

	// the amount of effect is adjusted by subtracting the
	// 1-t from the corrosion map. This causes the image
	// to "glide" over, with most visible parts first.
	float corrosion = texture(iChannel0, uv).r - u;

	// corrosion edge is controlled by speed of the change.
	float edge = clamp((1.0 - abs(corrosion)/0.1)*max(dt, 0.0), 0.0, 1.0);

	// clamp the channel between 0 and 1, give some reddish appearance to the edge by adding it to red channel.
	rgba.a = clamp(corrosion + edge, 0.0, 1.0);
	rgba.r += edge*0.2;

	// interpolate against gray with alpha channel, only required to "demonstrate" the effect here.
	rgba.rgb = rgba.rgb*rgba.a + texture(iChannel2, uv * vec2(1.0, -1.0)).rgb*(1.0-rgba.a);

	fragColor = rgba;
}
