//uncomment for an other illusion
//#define Dots

#ifdef Dots
	#define BGCol .5
#else
	#define BGCol .85
#endif

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = mod(fragCoord.xy/iResolution.y*5.,1.0);

	float col = 0.0;

	if(uv.y < 0.6 && uv.y > 0.4) col = BGCol;
	if(uv.x < 0.6 && uv.x > 0.4) col = BGCol;

	#ifdef Dots
		float l = length(uv-.5);
		if(l < .14) col = 1.0;
	#endif

	fragColor = vec4(col,col,col,1.0);
}
