#define RGBSHIFT
#define OLDSCREENLINES
#define NUMBER_LINES 269.

vec4 rgbShift( in vec2 p , in vec4 shift) {
    shift *= 1.02*shift.w - 1.0;
    vec2 rs = vec2(shift.x,-shift.y);
    vec2 gs = vec2(shift.y,-shift.z);
    vec2 bs = vec2(shift.z,-shift.x);

    //scanline kinda
    #if 1 
    //vec2 rip = vec2(pow(sin(iTime),5.)*sin(p.y*100.+iTime*10.),0);
    float slsp=1.3;
    vec2 rip = vec2(
        clamp(sin(slsp*iTime),0.,1.)*
        clamp(sin(slsp*iTime*3.),0.,1.)*
        clamp(sin(slsp*iTime*17.),0.,1.)*
        clamp(sin(slsp*iTime*13.),0.,1.)
                    *sin(p.y*600.*sin(iTime)+iTime*10.), 0);
    p+=rip;
    #endif

    float r = texture(iChannel0, p+rs/2., 0.0).x;
    float g = texture(iChannel0, p+gs/2., 0.0).y;
    float b = texture(iChannel0, p+bs/2., 0.0).z;


    return vec4(r,g,b,1.0)/3.;
}

float rand(vec2 co) { 
    return fract(sin(dot(co.xy ,vec2(12.98,78.23))) * 43758.54);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float numberLines = 269.;
	vec2 uv = fragCoord.xy / iResolution.xy;
    vec3 oldScreenLines = vec3(sin(uv.y*NUMBER_LINES+sin(iTime)*20.));

    #ifdef RGBSHIFT
		vec3 col = mix(rgbShift(uv, vec4(0.015, 0.0, 0.015, 0.0)).xyz, oldScreenLines, 0.01) ;
    #else
    	vec3 col = texture(iChannel0, uv).xyz;
    #endif

    //edge detection glitch
    #if 1
    vec2 ofs = vec2(0.01,0);
    float edsp = 1.; 
    float fac = clamp(sin(edsp*iTime*5.-0.6),0.,1.)*
                clamp(sin(edsp*iTime*7.-0.3),0.,1.);
    col *= 1.-fac;////
    col += 0.01*
        fac*
      (  texture(iChannel0, uv+ofs.xy).xyz - texture(iChannel0, uv-ofs.xy).xyz
        +texture(iChannel0, uv+ofs.yx).xyz - texture(iChannel0, uv-ofs.yx).xyz
    )/length(ofs);
    #endif

    float vign = pow(1.2-length(uv-vec2(0.5)),3.);
    col -= .028*rand( uv.xy * iTime);
    fragColor = vec4(col*2.5, 1.)*vign;
}

