float opS( float d1, float d2 )
{
    return max(-d2,d1);
}

vec2 opU( vec2 d1, vec2 d2 )
{
	return (d1.x<d2.x) ? d1 : d2;
}

vec3 opRep( vec3 p, vec3 c )
{
    return mod(p,c)-0.5*c;
}

vec3 opTwist( vec3 p )
{
    float  c = cos(10.0*p.y+10.0);
    float  s = sin(10.0*p.y+10.0);
    mat2   m = mat2(c,-s,s,c);
    return vec3(m*p.xz,p.y);
}
vec3 opRipple(vec3 p){
	return vec3(p.x,p.y+0.1*sin(p.x*10.+p.z*10.),p.z);	
}
vec3 opRot( vec3 p, float c, float s, vec3 ax )
{
    mat3 q;//inv rot
    if(ax.x > 0.){
        q[0][0] = 1.; q[0][1] = 0.; q[0][2] = 0.;
        q[1][0] = 0.; q[1][1] = c; q[1][2] = s;
        q[2][0] = 0.; q[2][1] =-s; q[2][2] = c;
    }else if(ax.y > 0.){
        q[0][0] = c; q[0][1] = 0.; q[0][2] =-s;
        q[1][0] = 0.; q[1][1] = 1.; q[1][2] = 0.;
        q[2][0] = s; q[2][1] = 0.; q[2][2] = c;
    }else{//z
        q[0][0] = c; q[0][1] = s; q[0][2] = 0.;
        q[1][0] =-s; q[1][1] = c; q[1][2] = 0.;
        q[2][0] = 0.; q[2][1] = 0.; q[2][2] = 1.;
    }
    return q*p;
}
