float calcAO( in vec3 pos, in vec3 nor )
{
	float occ = 0.0;
    float sca = 1.0;
    for( int i=0; i<5; i++ )
    {
        float hr = 0.01 + 0.12*float(i)/4.0;
        vec3 aopos =  nor * hr + pos;
        float dd = map( aopos ).x;
        occ += -(dd-hr)*sca;
        sca *= 0.95;
    }
    return clamp( 1.0 - 3.0*occ, 0.0, 1.0 );    
}

// Smooth HSV to RGB conversion 
vec3 hsv2rgb_smooth( in vec3 c )
{
    vec3 rgb = clamp( abs(mod(c.x*6.0+vec3(0.0,4.0,2.0),6.0)-3.0)-1.0, 0.0, 1.0 );

	rgb = rgb*rgb*(3.0-2.0*rgb); // cubic smoothing	

	return c.z * mix( vec3(1.0), rgb, c.y);
}

vec3 render( in vec3 ro, in vec3 rd )
{ 
    vec3 col = vec3(0.0, 0.0, .0) ;//+rd.y*0.3;
    vec2 res = castRay(ro,rd);
    float t = res.x;
	float m = res.y;
    if( m>-0.5 )
    {
        vec3 pos = ro + t*rd;
        vec3 nor = calcNormal( pos );
        vec3 ref = reflect( rd, nor );

        // material        
		//col = 0.45 + 0.35*sin( vec3(0.05,0.08,0.10)*(m-1.0) );
        col = vec3(10.05,0.08,0.10);
        if( m<1.5 )
        {
            //float f = mod( floor(3.0*pos.z) + floor(3.0*pos.x), 3.0);
            // col = 0.3 + 0.1*f*vec3(1.0);

            float f = mod( floor(4.*atan(sin(pos.x*3.),sin(pos.z*3.))), 2.0);
            vec3 hip = mix(
                hsv2rgb_smooth(vec3(iTime*0.101,0.49,0.4)),
                hsv2rgb_smooth(vec3(iTime*0.113,0.49,0.4)),
                f);
            col = hip;
            //col = mix(vec3(0.3),hip,clamp(pow(0.2*length(pos.xz),4.),0.,1.));

        }else if(m<10.5){
            col = vec3(0.05,10.08,10.10);
        }else if(m<11.5){
            col = vec3(10.05,0.08,10.10);
        }else if(m<12.5){
            col = vec3(10.05,10.08,0.10);
        }

        // lighitng        
        float occ = calcAO( pos, nor );
		vec3  lig = normalize( vec3(-0.4, 0.7, -0.6) );
		float amb = clamp( 0.5+0.5*nor.y, 0.0, 1.0 );
        float dif = clamp( dot( nor, lig ), 0.0, 1.0 );
        float bac = clamp( dot( nor, normalize(vec3(-lig.x,0.0,-lig.z))), 0.0, 1.0 )*clamp( 1.0-pos.y,0.0,1.0);
        float dom = smoothstep( -0.1, 0.1, 0.1*ref.y );
        float fre = 1.*pow( clamp(1.0+dot(nor,rd),0.0,1.0), 2.0 );
		float spe = pow(clamp( dot( ref, lig ), 0.0, 1.0 ),16.0);

        dif *= softshadow( pos, lig, 0.12, 5.5 );
        dom *= softshadow( pos, ref, 0.42, 5.5 );

		vec3 lin = vec3(0.0);
        lin += 1.30*dif*vec3(1.00,0.80,0.55);
		lin += 2.00*spe*vec3(1.00,0.90,0.70)*dif;
        lin += 0.40*amb*vec3(0.40,0.60,1.00)*occ;
        lin += 0.50*dom*vec3(0.40,0.60,1.00)*occ;
        lin += 0.50*bac*vec3(0.25,0.25,0.25)*occ;
        lin += 0.25*fre*vec3(1.00,1.00,1.00)*occ;
		col = col*lin;

    	col = mix( col, vec3(0.0,0.,.0), 1.-exp( -0.002*t*t*t ) );
    }

	return vec3( clamp(col,0.0,1.0) );
}

mat3 setCamera( in vec3 ro, in vec3 ta, float cr )
{
	vec3 cw = normalize(ta-ro);
	vec3 cp = vec3(sin(cr), cos(cr),0.0);
	vec3 cu = normalize( cross(cw,cp) );
	vec3 cv = normalize( cross(cu,cw) );
    return mat3( cu, cv, cw );
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 mo = iMouse.xy/iResolution.xy;
	float time = 15.0 + iTime;


    vec3 tot = vec3(0.0);
#if AA>1
    for( int m=0; m<AA; m++ )
    for( int n=0; n<AA; n++ )
    {
        // pixel coordinates
        vec2 o = vec2(float(m),float(n)) / float(AA) - 0.5;
        vec2 p = (-iResolution.xy + 2.0*(fragCoord+o))/iResolution.y;
#else    
        vec2 p = (-iResolution.xy + 2.0*fragCoord)/iResolution.y;
#endif

		// camera	
        //vec3 ro = vec3( -0.5+1.5*cos(0.1*time + 6.0*mo.x),
          //             1.0 + 2.0*mo.y,
            //           0.5 + 2.0*sin(0.1*time + 6.0*mo.x) );

        #if 1
        	//kinda "Kendrick Lamar - Humble" 4cam effect
        	float lmsp = 0.4;
        	float csh = 0.3*step(0.1,-sin(lmsp*iTime))
                *step(0.9,sin(lmsp*iTime*6.));
        	vec2 shift = vec2(0.22*csh*sign(sin(iTime*25.)),csh*sign(cos(iTime*25.)));
        #else
        	vec2 shift = vec2(0.);
        #endif

        vec3 ro = vec3( 2.2*cos(0.1*time + 6.0*mo.x+shift.x),
                       clamp(0.4 + 2.0*mo.y+shift.y,0.3,10.),
                        2.2*sin(0.1*time + 6.0*mo.x+shift.x) );
        vec3 ta = vec3( -0.0, 0.65, 0.0 );
        // camera-to-world transformation
        mat3 ca = setCamera( ro, ta, 0.0 );
        // ray direction
        vec3 rd = ca * normalize( vec3(p.xy,2.0) );

        // render	
        vec3 col = render( ro, rd );

		// gamma
        col = pow( col, vec3(0.4545) );

        tot += col;
#if AA>1
    }
    tot /= float(AA*AA);
#endif

    vec2 uv = fragCoord/iResolution.xy;

    float fac = 0.49;//[0,1]
    fragColor = fac*vec4( tot, 1.0 )+(1.-fac)*texture( iChannel0, uv );
}
