void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

    float TWOPI = 6.283185307179586;
	float BEAT = 60./ 137. ;

    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

        // loooooop
    float time = iTime;
    float noire = mod(time, BEAT);

    // Time varying pixel color
    vec3 col = vec3(exp(-noire*10.));
	if (iResolution.y<2000.) {
        float c=message(
            (fragCoord/iResolution.y-vec2(.1,.2))*8.);
        if(c>=0.){
            fragColor=vec4(c);return;
        }
    }

    float line  = step(mod(iTime*1.,1. ), uv.x);
    float line2 = step(mod(iTime*2.,1. ), uv.y);
    float line3 = step(uv.y,mod(iTime*8.,1. ));
    // Output to screen
    fragColor += vec4(col,1.0)+vec4(vec3(line, line2, line3),1.0);
}

