// Dave Hoskins. Feb 2014.
// Butchered Harekiet's code. Thanks Harekiet!

void mainImage( out vec4 O, in vec2 C )
{
	vec2 p = 2.0 * (C.xy-iResolution.xy*.5) / iResolution.y; 

	float r = dot( p, p ) * 1.5;
    vec3 c = vec3(sin(6.28318530718 * (4.0 * iTime + r * iResolution.y / 70.0)));
    c = mix(vec3(1.0, 0.0, 0.0), c, smoothstep(.0, .002, r)); //... red dot

	O = vec4( c, 1.0);	
}
