void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;
    vec2 m = vec2(iMouse.x, iMouse.y) / iResolution.xy;
    float ratio = iResolution.x / iResolution.y;

    vec2 pos = m;
    if(m.x < 0.01 && m.y < 0.1)
        pos = vec2(0.5);

    vec2 dxy = vec2(ratio, 1.0);
    float dist = distance(pos * dxy, uv * dxy);

    uv.y = 1.0-uv.y;
    uv -= vec2(0.5) ;
    uv *= dist*0.6;

	fragColor = texture(iChannel0, uv+vec2(0.5));
}
