void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float iteration = 400.0 + iTime;
    float x, y;
    float ratio = iResolution.x / iResolution.y;

    vec2 coord = vec2(fragCoord.xy / iResolution.xy);
    vec2 center = vec2(0.5, 0.5);

    x = length(coord - center) * 2.0 + 1.6 * sin(iteration * 0.5);

	fragColor = vec4(
        vec3(
            x,
            sin(x * iteration * (pow(x, cos(iteration * 0.2 + 0.2 * abs(sin(x)))))),
            1.0 - x + 0.5 * cos(iteration * 0.5)
        ),
        1.0);
}
