float rand(vec2 co){
    return fract(sin(dot(co.xy ,vec2(12.9898,78.233+iTime/10000.))) * 43758.5453);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{


    vec4 white = vec4(1.);
    vec4 red = vec4(0.65, 0.05, 0., 1.);
	vec2 uv = fragCoord.xy / iResolution.xy;
    vec2 center = vec2(.5);
  	vec2 centered = uv-center;
    centered.x*= iResolution.x/iResolution.y;
    float dist = length(centered);

    if(dist>0.4) red = white;


    float ratio = uv.y+0.4*rand(uv)-0.2;



	fragColor = mix(red, white, ratio);
}
