const highp float;

float zoom = 10.0;
float power = 3.0;

float func(float x, float y){
    float wave = sin(iTime*2.0)*1.7;
    float r = sqrt(x*x+y*y)*(sin(iTime*2.3)+4.0)/2.0, a = atan(y,x) - iTime/2.0;
    float n1 = sin(wave*r-5.0*a)-3.0 + r*0.6;
    wave = sin(iTime+3.1415926);
    float n2 = sin(wave*0.9*r+7.0*a)*1.4-3.0 + r;
    wave = sin(iTime+2.0/3.0*3.1415926)*1.6;
    float n3 = sin(wave*0.4*r+9.0*a)-3.0 + r;

    return n1+n2+n3;
}
float f(vec2 b) {
    float x = b.x, y = b.y;
    return func(y, x)-0.2;
}
float grad(vec2 p) {
    vec2 h = vec2( 0.001, 0.0 );
    vec2 g = vec2( f(p+h.xy) - f(p-h.xy),
                 f(p+h.yx) - f(p-h.yx) )/(30.0*h.x);
    return length(g);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
    vec2 coord = fragCoord.xy;
    vec2 c = (coord - vec2(iResolution)/2.0)/iResolution.x;
    float x = c.x*zoom, y = c.y*zoom;

    vec2 put = vec2(x, y);
    float a = f(put)/grad(put);

    float n = pow(1.0/(a+1.1), power);
    float n1 = 1.0-n;
    float r = sin(iTime)*0.5+0.5, g = sin(iTime+3.1415926*2.0/3.0)*0.5+0.5,
        b = sin(iTime+3.1415926*4.0/3.0)*0.5+0.5;
    float r1 = -sin(iTime)*0.5+0.5, g1 = -sin(iTime+3.1415926*2.0/3.0)*0.5+0.5,
        b1 = -sin(iTime+3.1415926*4.0/3.0)*0.5+0.5;
    n1 = 0.0;
    n = n*1.3;
    vec3 color = vec3((r*n+r1*n1), (g*n+g1*n1), (b*n+b1*n1));
    fragColor = vec4(color, 1);
}
