const float zoom = 10.0;
float t;
bool is;
float a1 = 1.0, a2 = 1.23, a3 = 1.54, a4 = 2.23, a5 = 1.54, a6 = 1.63;

float xCoord(float time){
    return zoom*cos(time*1.42784);
}
float yCoord(float time){
    return zoom*sin(time*2.42784);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
t = (iTime)/1.0;

    vec2 coord = fragCoord.xy;
    vec2 c = ((coord - vec2(iResolution)/2.0)/iResolution.x)*zoom;
    float x = c.x+xCoord(t), y = c.y+xCoord(t);

    vec2 c1 = vec2(cos(t*a1), sin(t*3.4321));
    c1 *= zoom;
    vec2 c2 = vec2(sin(t*a2), cos(t*3.0));
    c2 *= zoom;
    vec2 c3 = vec2(sin(t*a3), cos((t+1.0)*2.435));
    c3 *= zoom;
    vec2 c4 = -c1;
    vec2 c5 = -c2;
    vec2 c6 = -c3;

    float l1 = length(c1-c);
    float l2 = length(c1+c)/sin(t*2.23431);
    float l3 = length(c3-c)/cos(t*3.431);
    float l4 = length(c4-c)/sin(t*1.0543);
    float l5 = length(c5+c)/sin(t*2.23431);
    float l6 = length(c6-c)/cos(t*3.431);

    bool bo1 = (mod(floor(l1), 2.0) == 0.0) ^^ (mod(floor(l4), 2.0) == 0.0);
    bool bo2 = (mod(floor(l2), 2.0) == 0.0) ^^ (mod(floor(l5), 2.0) == 0.0);
    bool bo3 = (mod(floor(l3), 2.0) == 0.0) ^^ (mod(floor(l6), 2.0) == 0.0);

    float r = sin(iTime)*0.5+0.5, g = sin(iTime+3.1415926*2.0/3.0)*0.5+0.5,
        b = sin(iTime+3.1415926*4.0/3.0)*0.5+0.5;
    vec3 color = vec3(r, g, b);
    if(bo1) color.x = 1.0-color.x;
    if(bo2) color.y = 1.0-color.y;
    if(bo3) color.z = 1.0-color.z;
    fragColor = vec4(color, 1);
}
