void mainImage(out vec4 c, vec2 u)
{
    u = 2.*(u-.5*iResolution.xy) / iResolution.y;
    c = pow(texture(iChannel1,u+iTime*vec2(-.1,0))*texture(iChannel1,(u+iTime*vec2(-.1,0)+1.)*1.3),vec4(5));

    c = mix(
        	mix(
                (.5+.5*texelFetch(iChannel0, ivec2(vec2(acos(u.x/sqrt(1.-u.y*u.y))/6.28,u.y*.5+.5)*iResolution.xy),0))
                , vec4(.7,.8,1,1), pow(length(u),20.) )
        		* (smoothstep(-.1,1.,dot(normalize(vec3(3,1,2)),vec3(u.xy,sqrt(1.-dot(u,u)))))+.001)
        	, c, smoothstep(.99,1.,length(u)) );

    c = pow(c,vec4(1./2.2));
}

#define T(x) texture(iChannel0, fract((x)/iResolution.xy))

void mainImage(out vec4 c, vec2 u)
{   
    c=1./u.yyyx;

    vec2 m = iMouse.z>.0 ? 2.*iMouse.xy/iResolution.xy : vec2(.8,.9)-.2*cos(vec2(.1,.13)*iTime);

    for(float t=.6; t<4e2; t+=t)
    	c += c.gbar*m.x-c*m.y+T(u-c.wz*t);

	c = mix(T(u+c.xy), cos(c), .07);
}
