renderpass.code:
float factor=2.0;

vec2 thunder(vec2 v)
{
    v.x *= .5;
    return fract(v * exp2(ceil(-log2(v.y))));
}

vec2 hash(float p)
{
    return fract(sin(p * vec2(438.8, 452.8)) * 18.5453);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

    vec2 U = fragCoord/iResolution.xy;
    vec4 rnd0 = texture(iChannel0, U + iTime * .1);
    vec4 rnd1 = texture(iChannel0, .1 * U + iTime * .1);
    vec2 V = thunder(U * vec2(1., .75) + .1 * vec2(2. * rnd1.x - 1., 0.));
    float trigger = clamp(10.*pow(hash(iTime).x, 5.)-9.,0.,1.);
    trigger = max(trigger, 1.0 - smoothstep(0.0, 0.125, abs(iTime - 10.))); // force lightning for icon at 10s
    //o = vec4(4. * trigger * clamp(rnd1.y*(1. - smoothstep(0., .15 * rnd0.x, abs(4. * abs(V.x -.5) - (1. - V.y)))), 0., 1.));
    vec4 o = texture(iChannel0, U)*(0.8+trigger/2.0)+vec4(4. * trigger * (rnd1.y*(1. - smoothstep(0., .15 * rnd0.x, abs(4. * abs(V.x -.5) - (1. - V.y))))));


	vec2 uv = fragCoord.xy / iResolution.xy;
    vec3 color = texture(iChannel0, uv).xyz;
	float t=iTime/3.0;

    float average = (color.x + color.y + color.z) / 3.0;

    vec3 gray = vec3(average);
    float treshold = ((sin(t)+2.0)/2.5) *sin(uv.y+0.5)*sin(uv.x+1.);//

    if(average < treshold){
        color = vec3(0.1);
    }else {
    	color = mix(color, vec3(1,1,1), uv.x*uv.y);
    }


    float freq=texture(iChannel0,uv).x*texture(iChannel0,uv).y*110.0;

    vec3 tx=texture(iChannel0,uv).rgb*(factor*factor*freq)/factor;
         tx.r+=mod(0.+t/20.,.1+sin(t));

    fragColor = o/2.0+ vec4(tx.x/tx.y)*vec4(color,1.0)*texture(iChannel0, uv);
}
