void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord.xy / iResolution.xy;
    float zoom = ((sin(iTime)*0.5+0.55))*2.0;
    float x0 = (uv.x * 3.5 - 2.5) * 1.0/zoom;
    float y0 = (uv.y * 2.0 -1.0) * 1.0/zoom;
    float x = 0.0;
    float y = 0.0;
    int iteration = 0;
    const int max_iteration = 200;
    const float max_d_sq = 4.0;
    float xtemp;
    // while loops not supported on iPhone!!
    //while(x*x + y*y < max_d_sq && iteration < max_iteration) {
    for(int i = 0; i < max_iteration; i++) {
        xtemp = x*x - y*y + x0;
        y = 2.0*x*y + y0;
        x = xtemp;
        iteration = i;
        if(x*x + y*y > max_d_sq) break;
    }
    float c = 1.0-float(iteration)/float(max_iteration);
    fragColor = vec4(c, c, c, 1.0);
}
