void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // These should be uniforms
    float gridWidth = 0.2;
    vec2 fgScale = 1.0 / vec2(256.0, 32.0);
    vec2 bgScale = 1.0 / vec2(64.0, 64.0);
    // Get the coordinate, in pixels
    vec2 pixCoord = fragCoord.xy / iResolution.y * 32.0;
    // Get the foreground and background colors
    vec4 fgColor = texture(iChannel0, pixCoord * fgScale);
    vec4 bgColor = vec4(step(0.1, texture(iChannel1, pixCoord * bgScale)));
    // Calculate the grid
    vec2 gridPos = fract(pixCoord + gridWidth * 0.5);
    vec2 gridFrac = step(gridWidth, gridPos);
    fragColor = mix(bgColor, fgColor, gridFrac.x * gridFrac.y);
}
