void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    fragColor=texture(iChannel0, fragCoord/iResolution.xy); 
	fragColor.z=(fragColor.z+fragColor.x)*0.95;

    if((iFrame==0)||(length(iMouse.xy-fragCoord))<5.0)
    {
        fragColor.x=(fract(dot(sin(fragCoord*vec2(157.1,113.7)),
                               vec2(353.17+iTime,383.093-iTime)))>0.33)?0.0:1.0;
    }
    else
    {

		vec4 u=vec4(0.0);
    	u+=texture(iChannel0,(fragCoord+vec2(-1.0,-1.0))/iResolution.xy); 
    	u+=texture(iChannel0,(fragCoord+vec2( 0.0,-1.0))/iResolution.xy); 
	    u+=texture(iChannel0,(fragCoord+vec2( 1.0,-1.0))/iResolution.xy); 
  		u+=texture(iChannel0,(fragCoord+vec2(-1.0, 0.0))/iResolution.xy); 
  		u+=texture(iChannel0,(fragCoord+vec2( 1.0, 0.0))/iResolution.xy); 
		u+=texture(iChannel0,(fragCoord+vec2(-1.0, 1.0))/iResolution.xy); 
    	u+=texture(iChannel0,(fragCoord+vec2( 0.0, 1.0))/iResolution.xy); 
    	u+=texture(iChannel0,(fragCoord+vec2( 1.0, 1.0))/iResolution.xy);
     	if(u.x==3.0)
            fragColor.x=1.0;
        else if(u.x<2.0)
			fragColor.x=0.0;
        else if(u.x>3.0)
            fragColor.x=0.0;
    }
}
