void mainImage(out vec4 fragColor, in vec2 fragCoord)
{
    vec2 pos = iMouse.xy;
    float d = distance(pos, fragCoord.xy);
    float dm = d / 1.0;
    float freqScale = (sin(iTime)*0.5+0.5) * 0.2 + 0.01;
    vec3 c = vec3(cos(freqScale * d)*0.5+0.5 / dm);

    float orbitFreq = 0.5;
    float orbitRadius = 20.0;
    float theta = iTime * 2.0 * 3.1415926535897 * orbitFreq;
    float freqScaleScale = 2.0;

    const int iterations = 32;
    for(int i = 0; i < iterations; i++)
    {
        float ii = 1.0 + float(i);
        float fs = freqScale * freqScaleScale * ii;
        float dis = orbitRadius * ii;
        float p = ii * 0.025;

        vec2 pos2 = pos + vec2(cos(theta), sin(theta)) * dis;
        float d2 = distance(pos2, fragCoord.xy);
        float dim2 = d2 / (1.0 * sin(iTime)) / dis;
        c += cos(fs * d2)*0.5+0.5 / dim2 * vec3(1.0 - p, 1.0 + p, 0.9);

        vec2 pos3 = pos + vec2(-cos(theta), -sin(theta)) * dis;
        float d3 = distance(pos3, fragCoord.xy);
        float dim3 = d3 / (1.0 * -sin(iTime)) / dis;
        c += cos(fs * d3)*0.5+0.5 / dim3 * vec3(1.0 + p, 1.0 - p, 0.9);
    }

    c /= float(iterations * 2) + 1.0;
    c *= 1.25;
    c += 0.5;

    fragColor = vec4(c, 1.0);
}
