void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

    const float AMOUNT = 0.08;

    vec2 p = fragCoord.xy / iResolution.xy;

    //move displacement map    
    vec2 p2 = p + vec2(iTime/10.0);


    //offset texture sample posn

    //with sin
    //p.x = p.x + sin(p.y*80.+iTime*6.)*0.03;


    //with noise texture rb value    
    p += (texture(iChannel1, p2).rb-vec2(.5))* AMOUNT;

	fragColor = texture(iChannel0, p);
}
