void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec4 t = texture(iChannel0,fragCoord / iResolution.xy);
    if (t.x == 1.0) {
        fragColor = vec4(1.0, 0.0, 0.0, 1.0);
    } else {
		fragColor = vec4(0.0);
    }
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    fragColor=texture(iChannel0, fragCoord/iResolution.xy); 

    if((iTime<1.0))
    {
        vec2 dst = fragCoord.xy - (iResolution.xy/2.0);
        vec2 len = abs(dst);
        bool a = len.x < 5.0 || (dst.x > 0.0 && dst.x < 6.0);
        bool b = len.y < 5.0 || (dst.y > 0.0 && dst.y < 6.0);
        bool c = len.x < 1.0 && dst.x >= 0.0;
        bool d = len.y < 1.0 && dst.y >= 0.0;
        fragColor.x= (a&&d || b&&c) ? 1.0: 0.0;
    }
    else
    {

		vec4 u=vec4(0.0);
    	u+=texture(iChannel0,(fragCoord+vec2( 0.0,-1.0))/iResolution.xy); 
  		u+=texture(iChannel0,(fragCoord+vec2(-1.0, 0.0))/iResolution.xy); 
  		u+=texture(iChannel0,(fragCoord+vec2( 1.0, 0.0))/iResolution.xy); 
    	u+=texture(iChannel0,(fragCoord+vec2( 0.0, 1.0))/iResolution.xy); 
     	if(u.x == 1.0 || u.x == 3.0)
            fragColor.x=1.0;
        else
            fragColor.x=0.0;
    }
}
