void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	// Square pixels, coordinates centered on screen for both X and Y axis
  	vec2 xy = 0.5 - fragCoord / iResolution.y;
    xy.x += 0.4;

    // Draw a circle with a radius of 0.5 (Diameter of 1.0)
	fragColor = vec4 (length(xy) < .5 ? 1. : 0.); 
}
