// This is an adaption of this ioccc entry by Peter Eastman:
// http://www.ioccc.org/2011/eastman/eastman.c

void mainImage(out vec4 c,in vec2 C)
{
                 vec2 k=vec2(5.,
               5.2),R=iResolution
            .xy,e=R/R.y;C/=R.y;float
           a=.3046,b=.2,l,o=cos(a),i=
          sin(a),  r=min(C,e-C).x,q=.5;
         vec2 p=2.*mod(vec2(iTime)
        ,k)/k;p=min(p,2.-p);p.y*=p.y;p=(
        e-q)*p+q*q;vec2 h=(p-vec2(C.x,1.
        -C.y))*4.,m=mat2(o,-i,i,o)*h;c=(
        l=length(h))>1.?(b>C.y?vec4(C.y>
         r?0.:q): vec4(r<b?0.:length(h+
          vec2(a,b))<1.?q:.8)):(1.5-l)
            *(mod(floor(2. * (5.*p.x+
              m.x/sqrt(1.-m.y*m.y))
                )+floor(1.8*m.y),
                 2.)<q?vec4(1):

vec4(1,0,0,1));
}
