//#define ADDSIN
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;
    float val = mod(iTime*uv.x*9.234325+uv.y*uv.y
#ifdef ADDSIN
                    +sin(uv.x/uv.y)
#endif
                    , 1.0);
    fragColor = vec4(val*uv.x,val*uv.y,0.5-uv.x*uv.y/2.,1.0);
}
