float BLUR_RADIUS_MAX = 120.0;
float SPEED = 3.0;

vec4 BlurH (sampler2D source, vec2 size, vec2 uv, float radius) {

	if (radius >= 1.0)
	{
		vec4 C = vec4(0.0); 

		float width = 1.0 / size.x;

		float divisor = 0.0; 

		for (float x = -radius; x <= 0.0; x++)
		{
			C += texture(source, uv + vec2(x * width, 0.0));
			divisor++; 
		}

		return vec4(C.r / divisor, C.g / divisor, C.b / divisor, 1.0);
	}

	return texture(source, uv);
}

vec4 BlurV (sampler2D source, vec2 size, vec2 uv, float radius) {

	if (radius >= 1.0)
	{
		vec4 C = vec4(0.0); 

		float height = 1.0 / size.y;

		float divisor = 0.0; 

       for (float y = -radius; y <= radius; y++)
		{
			C += texture(source, uv + vec2(0.0, y * height));
			divisor++; 
		}

		return vec4(C.r / divisor, C.g / divisor, C.b / divisor, 1.0);
	}

	return texture(source, uv);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

    vec2 uv = fragCoord.xy / iResolution.xy;

    float percent = (sin(iTime * SPEED) + 1.0) / 2.0;
    float blurRadius = percent * BLUR_RADIUS_MAX;

    vec4 A = texture(iChannel0, uv, -1000.0) * (1.0-percent);
    	A += BlurH(iChannel0, iResolution.xy, uv, blurRadius) * (percent);	

	fragColor = A;
}
