void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 xy = fragCoord.xy;
    vec4 solidRed = vec4(0,0.0,0.0,1.0);
    if(iMouse.x > xy.x && iMouse.y > xy.y)
    {
        solidRed.r = xy.x/iResolution.x;
        solidRed.g = xy.y/iResolution.y;
    } else {
        solidRed.g = xy.x/iResolution.x;
        solidRed.r = xy.y/iResolution.y;
    } 
    fragColor = solidRed;
}
