void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;

    // ANIMATE
    float t = 0.0;
    t = iTime;

    // MOVE COORDINATE ORIGIN TO CENTRE
    uv -= vec2(0.5, 0.5);

    // 5a) ROTATE EVERYTHING AROUND ORIGIN
    // https://en.wikipedia.org/wiki/Rotation_matrix
    float rt = t * 0.5;
    mat2 rmt = mat2(cos(rt), -sin(rt), sin(rt), cos(rt));
    uv = rmt * uv;

    // 5b) WAVY DISTORTION EFFECT
	uv.y += sin(uv.x * 12. + t) * 0.25;
	uv.x += sin(uv.y * 12. + t) * 0.25;

	// 4) CLONE THE IMAGE SEVERAL TIMES
    uv *= 3.0;
    uv = mod(uv, 1.0);

    // BLUE BACKGROUND
    fragColor = vec4(0.,0.,1.,1.);

    // 1) RIGHT SIDE IS RED
    if (uv.x > 0.5) fragColor = vec4(1.,0.,0.,1.);

    // 2) UPPER HALF ADDS GREEN
    if (uv.y > 0.5) fragColor.y = 1.;

    // 3) OUTSIDE CIRCLE SWIZZLE COLOURS AROUND
    if (distance(uv, vec2(0.5)) > 0.25) fragColor.xyz = fragColor.zxy;
}
