bool letterB(ivec2 n)
{
	return n.x==0 && n.y<5 && n.y>-1 || n.x==1 && (n.y==0 || n.y==2 || n.y==4) || n.x==2 && (n.y==0 || n.y==1 || n.y==3);
}

bool letterR(ivec2 n)
{
	return n.x==0 && n.y<5 && n.y>-1 || n.x==1 && (n.y==4 || n.y==2) || n.x==2 && (n.y==0 || n.y==1 || n.y==3);
}

bool letterO(ivec2 n)
{
	return (n.x==0 || n.x==2) && n.y<5 && n.y>-1 || n.x==1 && (n.y==0 || n.y==4);
}


bool letterP(ivec2 n)
{
	return n.x==0 && n.y<5 && n.y>-1 || n.x==1 && (n.y==4 || n.y==2) || n.x==2 && (n.y==3);
}

bool letterM(ivec2 n)
{
	return n.x==1 && n.y==3 || (n.x==0 || n.x==2) && n.y<5 && n.y>-1;
}


bool letterN(ivec2 n)
{
	return (n.x==0 || n.x==2) && n.y<5 && n.y>-1 || n.x==1 && (n.y==4);
}


bool letterU(ivec2 n)
{
	return (n.x==0 || n.x==2) && n.y<5 && n.y>-1 || n.x==1 && (n.y==0);
}


bool letterI(ivec2 n)
{
	return (n.x==0 || n.x==2) && (n.y==4 || n.y==0) || n.x==1 && (n.y<5 && n.y>-1);
}


bool letterE(ivec2 n)
{
	return (n.x==0) && n.y<5 && n.y>-1 || n.x==1 && (n.y==0 || n.y == 2 || n.y==4) || n.x==2 && (n.y==0 || n.y==4);
}

bool letterC(ivec2 n)
{
	return (n.x==0) && n.y<5 && n.y>-1 || n.x==1 && (n.y==0 || n.y==4) || n.x==2 && (n.y==0 || n.y==4);
}

bool letterA(ivec2 n)
{
	return (n.x==0 || n.x==2) && n.y<5 && n.y>-1 || n.x==1 && (n.y==4 || n.y == 2);
}

float sinm(float t, float vmin, float vmax)
{
	return (vmax-vmin)*0.5*sin(t)+vmin+(vmax-vmin)*0.5;
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 p = fragCoord.xy / iResolution.xx;
	p -= vec2(0.5,0.5*iResolution.y/iResolution.x);
	float t = iTime;
	p *= sin(7.0*t)*20.0+40.0;
//	p.x -= 10.5;
	vec2 n = floor(p);
	vec2 f = fract(p);

	float dist = 0.0;


	dist = 1.0/length(p-(n+0.5));

	n += 5.0;
	n.x += 5.00;

	ivec2 ni = ivec2(n.x, n.y-6.0);

	float scale = 0.35;

	if(sin(t*4.0)>0.0)
	{
		dist *= scale;
		scale = 1.0/scale;
	}

	if( letterM(ni) || letterE(ivec2(ni.x-4,ni.y)) || letterR(ivec2(ni.x-8,ni.y)) || letterC(ivec2(ni.x-12,ni.y)) || letterI(ivec2(ni.x-16,ni.y)))
		dist *= scale;

	ni = ivec2(n.x+5.0, n.y);

	if( letterB(ni) || letterE(ivec2(ni.x-4,ni.y)) || letterA(ivec2(ni.x-8,ni.y)) || letterU(ivec2(ni.x-12,ni.y)) || letterC(ivec2(ni.x-16,ni.y)) || letterO(ivec2(ni.x-20,ni.y)) || letterU(ivec2(ni.x-24,ni.y)) || letterP(ivec2(ni.x-28,ni.y)))
		dist *= scale;

	//vec3 c1 = vec3(0.43,0.33,0.24);
	vec3 c2 = vec3(0.45,0.43,0.2);

	vec3 c1 = vec3(0.25,0.33,0.4);
	//vec3 c2 = vec3(0.525,0.2633,0.24);

	vec3 c=c1;
	if(sin(t*2.0)>0.0)
		c = c2;
	fragColor = vec4(c*dist,1.0);
}
