#define TAU 6.2831853072
#define GAMMA 0.6
#define RADIUS 0.2
#define THICKNESS 0.01

vec2 movepoint( vec3 trajectory )
{
	return vec2( 0.5*sin( trajectory.x*iTime*0.25 + trajectory.z ) + 0.5, 
				 0.5*cos( trajectory.y*iTime*0.25 + trajectory.z ) + 0.5 );
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;

	vec2 point[3];
	float len[3];
	float sum[1];

	point[0] = movepoint( vec3( 2.0, 3.0, TAU * 1.0 / 3.0) );
	point[1] = movepoint( vec3(-5.0, 4.0, TAU * 2.0 / 3.0) );
	point[2] = movepoint( vec3( 6.0,-7.0, TAU * 3.0 / 3.0) );

	len[0] = distance( point[0], uv );
	len[1] = distance( point[1], uv );
	len[2] = distance( point[2], uv );

	sum[0] = pow( len[0] * len[1] * len[2], GAMMA );
	sum[0] -= RADIUS;
	sum[0] = abs(sum[0]);
	sum[0] = smoothstep(0.0,THICKNESS, sum[0]);
	fragColor = vec4( vec3(sum[0]), 1.0 );



}
