void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;
    float speed = 5.;
    float width = .1;
    float xMin = ((sin(iTime*speed)+1.-width)/2.) ;
    float xMax = xMin + width;

    bool a = uv.x > xMin && uv.x < xMax;


    fragColor = vec4(.4,.8,1,1.0);
    fragColor = vec4(vec3(.9),1.0);
    //fragColor = vec4(vec3(uv.y),1.0);
	//fragColor = vec4(vec3(sin(iTime),cos(iTime),tan(iTime)),1.0);

    if(a)
        fragColor = vec4(1,1,1,1.0);

}
