void mainImage( out vec4 o, vec2 p ) {
    o -= o;
    vec3 h, r = iResolution.xyz; float i;
    while( i++<1e2 )
		h = fract( iDate.w/20. * sin( i * vec3( 1, 3, 9 ) ) ),
   		o = max( o, h.xyzz * clamp(  r.y/39. * ( 1. + sin( 6.3 * h.z ) ) - length( p - r.xy * h.xy ), 0., 1. ) );
}
